INSERT INTO `generator_template` VALUES ('a85cf2be94ba431282c872b03fae608a', '控制器', 'controller', '1', 'cn.jeeweb.controller', 'E:\\testgen', 'package ${targetPackage};\r\n\r\nimport cn.jeeweb.bbs.aspectj.annotation.Log;\r\nimport cn.jeeweb.bbs.aspectj.enums.LogType;\r\nimport cn.jeeweb.bbs.common.bean.ResponseError;\r\nimport ${iservice.targetPackage}.I${entityName?cap_first}Service;\r\nimport ${entity.targetPackage}.${entityName?cap_first};\r\nimport cn.jeeweb.common.http.PageResponse;\r\nimport cn.jeeweb.common.http.Response;\r\nimport cn.jeeweb.common.mvc.annotation.ViewPrefix;\r\nimport cn.jeeweb.common.mvc.controller.BaseBeanController;\r\nimport cn.jeeweb.common.mvc.entity.tree.BootstrapTreeHelper;\r\nimport cn.jeeweb.common.mvc.entity.tree.BootstrapTreeNode;\r\nimport cn.jeeweb.common.mvc.entity.tree.TreeSortUtil;\r\nimport cn.jeeweb.common.mybatis.mvc.wrapper.EntityWrapper;\r\nimport cn.jeeweb.common.query.annotation.PageableDefaults;\r\nimport cn.jeeweb.common.query.data.PropertyPreFilterable;\r\nimport cn.jeeweb.common.query.data.QueryPropertyPreFilter;\r\nimport cn.jeeweb.common.query.data.Queryable;\r\nimport cn.jeeweb.common.query.utils.QueryableConvertUtils;\r\nimport cn.jeeweb.common.security.shiro.authz.annotation.RequiresMethodPermissions;\r\nimport cn.jeeweb.common.security.shiro.authz.annotation.RequiresPathPermission;\r\nimport cn.jeeweb.common.utils.ObjectUtils;\r\nimport cn.jeeweb.common.utils.StringUtils;\r\nimport com.alibaba.fastjson.JSON;\r\nimport com.alibaba.fastjson.serializer.SerializeFilter;\r\nimport org.springframework.beans.factory.annotation.Autowired;\r\nimport org.springframework.stereotype.Controller;\r\nimport org.springframework.ui.Model;\r\nimport org.springframework.validation.BindingResult;\r\nimport org.springframework.web.bind.annotation.*;\r\nimport org.springframework.web.servlet.ModelAndView;\r\n\r\nimport javax.servlet.http.HttpServletRequest;\r\nimport javax.servlet.http.HttpServletResponse;\r\nimport java.io.IOException;\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\n\r\n/**\r\n * All rights Reserved, Designed By www.jeeweb.cn\r\n *\r\n * @version V1.0\r\n * @package ${targetPackage}\r\n * @title: ${functionName}控制器\r\n * @description: ${functionDesc}控制器\r\n * @author: ${functionAuthor}\r\n * @date: ${time}\r\n * @copyright: 2018 www.jeeweb.cn Inc. All rights reserved.\r\n */\r\n\r\n@RestController\r\n@RequestMapping(&quot;${entityName?cap_first}&quot;)\r\n@ViewPrefix(&quot;modules/${entityName?cap_first}&quot;)\r\n@RequiresPathPermission(&quot;${entityName?cap_first}&quot;)\r\n@Log(title = &quot;${functionName}&quot;)\r\npublic class ${entityName?cap_first}Controller extends BaseBeanController&lt;${entityName?cap_first}&gt; {\r\n\r\n    @Autowired\r\n    private I${entityName?cap_first}Service ${entityName?uncap_first}Service;\r\n\r\n\r\n    @GetMapping\r\n    @RequiresMethodPermissions(&quot;view&quot;)\r\n    public ModelAndView list(Model model, HttpServletRequest request, HttpServletResponse response) {\r\n        return displayModelAndView(&quot;list&quot;);\r\n    }\r\n\r\n    /**\r\n     * 根据页码和每页记录数，以及查询条件动态加载数据\r\n     *\r\n     * @param request\r\n     * @param response\r\n     * @throws IOException\r\n     */\r\n    @RequestMapping(value = &quot;ajaxList&quot;, method = { RequestMethod.GET, RequestMethod.POST })\r\n    @PageableDefaults(sort = &quot;id=desc&quot;)\r\n    @Log(logType = LogType.SELECT)\r\n    @RequiresMethodPermissions(&quot;list&quot;)\r\n    public void ajaxList(Queryable queryable, PropertyPreFilterable propertyPreFilterable, HttpServletRequest request,\r\n                          HttpServletResponse response) throws IOException {\r\n        EntityWrapper&lt;${entityName?cap_first}&gt; entityWrapper = new EntityWrapper&lt;&gt;(entityClass);\r\n        propertyPreFilterable.addQueryProperty(&quot;id&quot;);\r\n        // 预处理\r\n        QueryableConvertUtils.convertQueryValueToEntityValue(queryable, entityClass);\r\n        SerializeFilter filter = propertyPreFilterable.constructFilter(entityClass);\r\n        PageResponse&lt;${entityName?cap_first}&gt; pagejson = new PageResponse&lt;&gt;(${entityName?uncap_first}Service.list(queryable,entityWrapper));\r\n        String content = JSON.toJSONString(pagejson, filter);\r\n        StringUtils.printJson(response,content);\r\n    }\r\n\r\n    @GetMapping(value = &quot;add&quot;)\r\n    public ModelAndView add(Model model, HttpServletRequest request, HttpServletResponse response) {\r\n        model.addAttribute(&quot;data&quot;, new ${entityName?cap_first}());\r\n        return displayModelAndView (&quot;edit&quot;);\r\n    }\r\n\r\n    @PostMapping(&quot;add&quot;)\r\n    @Log(logType = LogType.INSERT)\r\n    @RequiresMethodPermissions(&quot;add&quot;)\r\n    public Response add(${entityName?cap_first} entity, BindingResult result,\r\n                           HttpServletRequest request, HttpServletResponse response) {\r\n        // 验证错误\r\n        this.checkError(entity,result);\r\n        ${entityName?uncap_first}Service.insert(entity);\r\n        return Response.ok(&quot;添加成功&quot;);\r\n    }\r\n\r\n    @GetMapping(value = &quot;{id}/update&quot;)\r\n    public ModelAndView update(@PathVariable(&quot;id&quot;) String id, Model model, HttpServletRequest request,\r\n                               HttpServletResponse response) {\r\n        ${entityName?cap_first} entity = ${entityName?uncap_first}Service.selectById(id);\r\n        model.addAttribute(&quot;data&quot;, entity);\r\n        return displayModelAndView (&quot;edit&quot;);\r\n    }\r\n\r\n    @PostMapping(&quot;{id}/update&quot;)\r\n    @Log(logType = LogType.UPDATE)\r\n    @RequiresMethodPermissions(&quot;update&quot;)\r\n    public Response update(${entityName?cap_first} entity, BindingResult result,\r\n                           HttpServletRequest request, HttpServletResponse response) {\r\n        // 验证错误\r\n        this.checkError(entity,result);\r\n        ${en